import React, {createContext,useReducer} from "react";
import AppReducer from './AppReducer';
import axios from 'axios';

const initialState={
    
    posts:[],
    error:null


}


// Create Context

export const GlobalContext = createContext(initialState);

export const GlobalProvider= ({children}) => {

 const [state,dispatch] = useReducer(AppReducer,initialState)


 // Actions

 async function getPosts(){

    const { data } = await axios.get('http://localhost:5000/posts');
   console.log(data)
    try {
        
        dispatch({
            type: 'FETCH_ALL',
            payload: data
    
          });

    } catch (error) {
        
        dispatch({
            type: 'ERROR',
            payload: error
          });

    }

 }

     async function createPost(post) {
           
        const config = {
          
            headers: {
                'Content-Type' : 'application/json'
            }

        }
        
    
        try {
            const res = axios.post('http://localhost:5000/posts',post,config);
                
            dispatch({
                    
                type: 'CREATE_POST',
                payload: res.data.data

            })

        } catch (error) {
         
            dispatch({
               
                type: 'FAILED',
                payload: error

            });

        }


     }


     async function updatePost(_id,updatedPost){
         
        try {

          const {data}  = axios.patch(`http://localhost:5000/posts/${_id}`,updatedPost);

           dispatch({
               type:'UPDATE',
               payload: data
           })
            
        } catch (error) {
          console.log(error)
        }
         

     }

     async function deletePost(_id){

        try {


           await axios.delete(`http://localhost:5000/posts/${_id}`);

            dispatch({
                    
                type: 'DELETE',
                payload: _id

            })
            
        } catch (error) {
            console.log(error);
        }

     }


      return(
<GlobalContext.Provider value={{
    posts:state.posts,
    getPosts,
    createPost,
    deletePost,
    updatePost
}
}>
  {children}
</GlobalContext.Provider>
         

      )


}
